function Todo() {
   const [text,setText] = useState('')
   const [Items,setItems] = useState([]);
   function changeText(e){
     setText(e.target.value)
   }
   function addItems(){
    // setItems([...Items,text])
    // console.log(Items)
    if(text===" ")
    {
        
    }
    else{

        setItems([...Items,text]);
        setText("")
    }
    // console.log(Items)
    // console.log(Items.map(num)=>num)
   }
    return (
        <div className='Main_div'>
            <div className="Center_div">
                <h1>ToDo List</h1>
                <br />
                <input type="text"value={text} onChange={changeText} placeholder='Add a Item' className='input'  />
                <button className='btn' onClick={addItems}> + </button>
                <br />
                <ol>

    
                    {/* <li>{Items}</li> */}
                   { Items.map((item,key)=>{
                        return<li>{item}</li>
                    })}
                    {/* <li>{text}</li> */}
                    {/* {Items.map((item)=>{
                      return<li>{item}</li>
                    })}  */}
                </ol>
            </div>
        </div>
    );
}

export default Todo;
